// Extract data from the webhook request
const message = $input.first().json.message;
const userId = $input.first().json.userId;
const sessionId = $input.first().json.sessionId;
const userInfo = $input.first().json.userInfo;

// Simple response logic - you can replace this with AI, FAQ, or routing logic
let response = "Hi! How can I help you today?";
let messageType = "bot";

// Example: Check for keywords and provide responses
if (message.toLowerCase().includes('help')) {
  response = "I'm here to help! What specific issue are you facing?";
} else if (message.toLowerCase().includes('pricing')) {
  response = "Our pricing plans start at $9/month. Would you like to know more?";
} else if (message.toLowerCase().includes('contact')) {
  response = "You can reach us at support@example.com or call us at 1-800-123-4567.";
} else if (message.toLowerCase().includes('thanks') || message.toLowerCase().includes('thank you')) {
  response = "You're welcome! Is there anything else I can help you with?";
}

// Create the response object
const responseData = {
  response: response,
  messageId: `msg_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  timestamp: new Date().toISOString(),
  type: messageType,
  sessionId: sessionId
};

return [{ json: responseData }];
